@testable
import Leetcode
import XCTest

final class LongestSubstringWithAtLeastKRepeatingCharactersTests: XCTestCase {
  func testLongestsubstring1() {
    let input = ("aaabb", 3)
    let output = 3
    XCTAssertEqual(LongestSubstringWithAtLeastKRepeatingCharacters().longestSubstring(input.0, input.1), output)
  }

  func testLongestsubstring2() {
    let input = ("ababbc", 2)
    let output = 5
    XCTAssertEqual(LongestSubstringWithAtLeastKRepeatingCharacters().longestSubstring(input.0, input.1), output)
  }

  func testLongestsubstring3() {
    let input = ("aaabbb", 3)
    let output = 6
    XCTAssertEqual(LongestSubstringWithAtLeastKRepeatingCharacters().longestSubstring(input.0, input.1), output)
  }

  func testLongestsubstring4() {
    let input = ("", 301)
    let output = 301
    XCTAssertEqual(LongestSubstringWithAtLeastKRepeatingCharacters().longestSubstring(input.0, input.1), output)
  }
}
