@testable
import Leetcode
import Testing

@Suite
struct LongestSubstringWithAtLeastKRepeatingCharactersTests {
  @Test func testLongestsubstring1() {
    let input = ("aaabb", 3)
    let output = 3
    #expect(LongestSubstringWithAtLeastKRepeatingCharacters().longestSubstring(input.0, input.1) == output)
  }

  @Test func testLongestsubstring2() {
    let input = ("ababbc", 2)
    let output = 5
    #expect(LongestSubstringWithAtLeastKRepeatingCharacters().longestSubstring(input.0, input.1) == output)
  }

  @Test func testLongestsubstring3() {
    let input = ("aaabbb", 3)
    let output = 6
    #expect(LongestSubstringWithAtLeastKRepeatingCharacters().longestSubstring(input.0, input.1) == output)
  }

  @Test func testLongestsubstring4() {
    let input = ("", 301)
    let output = 301
    #expect(LongestSubstringWithAtLeastKRepeatingCharacters().longestSubstring(input.0, input.1) == output)
  }
}
